  CREATE OR REPLACE PROCEDURE "XE"."PROC_DUMMY_BOARD" (
    p_count IN NUMBER
) AS
BEGIN
    DELETE FROM tbl_board
    WHERE
        1 = 1;

    DELETE FROM tbl_reply
    WHERE
        1 = 1;

    FOR cnt IN 1..p_count LOOP IF ( cnt <= p_count / 2 ) THEN
        INSERT INTO tbl_board VALUES ( -- 공지사항
            cnt,
            '공지사항 제목',
            '공지사항 내용',
            'admin',
            0,
            0,
